// A helper for the node-red chart.js template node
// see the template node for the majority of the user defined data
// here all that is needed is to setup values to limit the length of the chart
// Firstly chartMaxPoints may be set non-zero and (for each channel) when the number
// of points exceeds this value then the oldest samples will be discarded.
// Alternatively (or in addition) if the x axis is time based then chartTimeSpan (milliseconds)
// may be set and then old samples will be discarded to limit the chart to this time span.

var chartMaxPoints = 0;             //max no points in a chart before they drop off the left. Set to zero to disable
var chartTimeSpan = 60*60*1000;     //chart time span 4 mins. Set to zero to disable

// ----------------------------------------------------------
// You should not need to change anything below here

// with inputs connected to the same nodes as a chart.js node this maintains a chart history
// for preloading into the chart when it is opened in the browser

var topicsOfinterest = context.get('topicsOfInterest');    // can't use null here
if (typeof topicsOfInterest == 'undefined') topicsOfInterest = null;

var msg2 = null;
var datasets = context.get('datasets') || [];
// each dataset maps to a channel on the chart and consists of a hash with fields
// topic: the topic name of the channel
// data: an array of data points {x: x_value, y: y_value}
// If msg.action == "preload" then the topic and payload are ignored and a message is passed
// on containing the current chart data and msg.action still set to preload

if (msg.action === "preload") {
    // we should have been passed the topics of interest
    topicsOfInterest = msg.topics;      // null if not provided
    context.set('topicsOfInterest', topicsOfInterest);
    // send a message containing the current chart data time range values, leave rest of msg as is
    msg.payload = datasets;
    msg.chartMaxPoints = chartMaxPoints;
    msg.chartTimeSpan = chartTimeSpan;
} else if (msg.action === "restore") {
    // restoring from persistent memory
    // step through the given datasets, if any
    if (Array.isArray(msg.payload)) {
        for (var i = 0; i < msg.payload.length; i++) {
            topic = msg.payload[i].topic;
            // find the matching topic
            var found = false;
            for (var k = 0; k < datasets.length; k++) {
                if (datasets[k].topic == topic) {
                    // insert the data in front of any that has been acquired since startup
                    datasets[k].data = msg.payload[i].data.concat(datasets[k].data);
                    found = true;
                    break;
                }
            }
            if (!found) {
                // new topic so make a new dataset and fill it
                datasets.push({topic: topic, data: msg.payload[i].data});
            }
        }
    }
    // remember that we have restored in order to enable future writes to persistent store
    context.set("restored", true);
    // send a preload action to the chart
    msg.action = "preload";
    msg.payload = datasets;
} else if (msg.action === "load") {
    // replace existing data for matching topics
    for (var j = 0; j < msg.payload.length; j++) {
        var topic = msg.payload[j].topic;
        // find it in the chart
        found = false;
        for (i = 0; i < datasets.length; i++) {
            if (datasets[i].topic == topic) {
                datasets[i].data = msg.payload[j].data;
                found = true;
                break;
            }
        }
        if (!found) {
            // new topic so make a new dataset and fill it
            datasets.push({topic: topic, data: msg.payload[j].data});
        }
    }
    msg = null;         // don't send anything on
} else {
    // check whether the topic is one we are interested in
    if (topicsOfInterest === null  || topicsOfInterest.indexOf(msg.topic) >= 0) {
        found = false;
        // find the right dataset if it already exists
        for (i = 0; i < datasets.length; i++) {
            dataset = datasets[i];
            if (dataset.topic == msg.topic) {
                found = true;
                dataset.data.push(msg.payload);
                break;
            }
        }
        if (!found) {
            // new topic so make a new dataset
            datasets.push({topic: msg.topic, data: [msg.payload]});
        }
    } else {
        // ignore topics that are not of interest
    }
    // do not pass on a message to o/p 1, send the datasets to o/p 2 for persistent
    // storage if required and we have been given data on startup
    // this is to prevent the persistent store being overwritten before it has been read
    msg = null;
    if (context.get("restored")) {
        msg2 = {payload: datasets};
    }
}

// remove any points that are too old
if (chartTimeSpan > 0) {
    var now = new Date();
    var oldestTimeAllowed = new Date();
    oldestTimeAllowed.setTime(now.getTime() - (chartTimeSpan));
    for (i = 0; i < datasets.length; i++) {
        var data = datasets[i].data;
        while (data.length > 0 ) {
            // convert the x value (in place) to a Date if it is a string
            if (typeof data[0].x === "string") {
                data[0].x = new Date(data[0].x);
            }            
            if (data[0].x < oldestTimeAllowed) {
                data.shift();
            } else {
                break;
            }
        }
    }
}
// strip samples off the front if there are now too many
// charTimeSpan == 0 implies don't do it
if (chartMaxPoints > 0) {
    var shifted = false;
    for (i = 0; i < datasets.length; i++) {
        data = datasets[i].data;
        while(data.length > chartMaxPoints) {
            data.shift();
        }
    }
}
context.set('datasets', datasets);
return [msg,msg2];
